mapper.update:
UPDATE ATTEMDANCE
SET ATT_STATE = 'N'
WHERE ATT_CODE = #{attCode}

UPDATE ATTEMDANCE
SET ATT_STATE = 'Y'
WHERE ATT_CODE = #{attCode}

UPDATE EMPLOYEE
SET VACATION = VACATION - #{empAttCount}
WHERE EMP_NO = #{empNo}

UPDATE EMPLOYEE
SET VACATION = VACATION + #{empAttCount}
WHERE EMP_NO = #{empNo}

mapper.delete:
DELETE EMP_ATTEMDANCE
WHERE EMP_ATT_TIME = #{empAttTime}
  AND EMP_NO = #{empNo}
  AND ATT_CODE = #{attCode}

mapper.update:
UPDATE EMP_ATTEMDANCE
SET EMP_ATT_STATE = 'Y'
WHERE EMP_ATT_TIME = #{empAttTime}
  AND EMP_NO = #{empNo}
  AND ATT_CODE = #{attCode}

UPDATE EMP_ATTEMDANCE
SET EMP_ATT_STATE = 'N'
WHERE EMP_ATT_TIME = #{empAttTime}
  AND EMP_NO = #{empNo}
  AND ATT_CODE = #{attCode}
